<?php/** * The template for displaying all pages. * * This is the template that displays all pages by default. * Please note that this is the wordpress construct of pages * and that other 'pages' on your wordpress site will use a * different template. * */?><?php get_header(); ?><div class="page-content">    <div class="grid_sub_16 sub_alpha">        <div class="content-bar">            <?php if (have_posts()) : the_post(); ?>                <h1 class="page_title"><?php printf(__('Tag Archives: %s', 'elite-lite'), '' . single_cat_title('', false) . ''); ?></h1>                <?php the_content(); ?>                <?php wp_link_pages(array('before' => '<div class="clear"></div><div class="page-link"><span>' . __('Pages:', 'elite-lite') . '</span>', 'after' => '</div>')); ?>                <div class="clear"></div>                <nav id="nav-single"> <span class="nav-previous">                        <?php next_posts_link(__('&larr; Older posts', 'elite-lite')); ?>                    </span> <span class="nav-next">                        <?php previous_posts_link(__('Newer posts &rarr;', 'elite-lite')); ?>                    </span> </nav>            <?php endif; ?>        </div>    </div>    <div class="grid_sub_8 sub_omega">        <?php get_sidebar(); ?>    </div></div></div><?php get_footer(); ?>